import React, { useState, useRef } from "react";
import slugify from "react-slugify";
import { useParams } from "react-router-dom";
import SunEditor from "suneditor-react";

import "suneditor/dist/css/suneditor.min.css"; // Import Sun Editor's CSS File
// import axios from "axios";
import Modal from "@mui/material/Modal";
import { Redirect } from "react-router-dom";
//
import Admin from "../Admin";
import ImagesWIndow from "../../../Components/ImagesWIndow/ImagesWIndow";
import NewCategory from "../../../Components/NewCategory";

import "./NewPost.css";
import { useMutation, useQuery } from "@apollo/client";
import { GET_ALL_CATEGORIES } from "../../../Graphql/Queries";
// import { CREATE_POST } from "../../../Graphql/Mutations";

export default function UpdatePost(props) {
  const nameParams = useParams();
  let idNumber = nameParams.id;

  const [namePost, setNamePost] = useState("");
  const [content, setContent] = useState("");
  const [thumb, setThumb] = useState("");
  const [category, setCategory] = useState("");
  const [slide, setSlide] = useState(false);
  const [middle, setMiddle] = useState(false);
  const [gameplay, setGameplay] = useState(false);
  const [publicPost, setPublicPost] = useState(false);
  const [midSection, setMidSection] = useState(false);
  // const [file, setFile] = useState("");
  const [modalShow, setModalShow] = useState(false);
  const editor = useRef(null);

  function dataForm(data) {
    setContent(data);
  }

  // useEffect(() => {
  //   let image = file.name;
  //   const url = `${base_url}/upload/${image}`;
  //   const data = new FormData();
  //   data.append("file", file);
  //   const options = {
  //     headers: {
  //       "Content-Type": "multipart/form-data",
  //     },
  //   };
  //   axios.post(url, data, options);
  // }, [file]);

  const { data: categories } = useQuery(GET_ALL_CATEGORIES);

  // const [updatePost, { data: isCreated }] = useMutation(CREATE_POST);

  // useEffect(() => {
  //   if (isCreated && isCreated.createPost.id) {
      // <Redirect to={`/admin/updatepost/${isCreated.createPost.id}`} />;
  //   }
  // }, [isCreated]);

  return (
    <div>
      <Admin>
        <div className="updateContent">
          <div className="updateContent-field">
            <button
            onClick={updatePost({
              variables: {
                id: idNumber,
                name: namePost,
                content: content,
                category: category,
                slug: slugify(namePost, { delimiter: "-" }),
                slide: slide,
                middle: middle,
                gameplay: gameplay,
                publicPost: publicPost,
                midSection: midSection,
                thumb: thumb,
              },
            })}
            >
              Atualizar
            </button>
            Nome:
            <input type="text" onChange={(e) => setNamePost(e.target.value)} value={namePost} />
            <div>
              Publico
              {/* <input type="checkbox" onClick={(e) => setPublicPost(e.target.checked)} /> */}
            </div>
            <div>
              Slide
              <input type="checkbox" onClick={(e) => setSlide(e.target.checked)} />
              Meio
              <input type="checkbox" onClick={(e) => setMiddle(e.target.checked)} />
              Gameplay
              <input type="checkbox" onClick={(e) => setGameplay(e.target.checked)} />
              midSection
              <input type="checkbox" onClick={(e) => setMidSection(e.target.checked)} />
            </div>
            <NewCategory />
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
              <option>Escolha uma Categoria</option>
              {categories &&
                categories.getAllCategories.map((cat, i) => (
                  <option key={i}>{cat.name.toUpperCase()}</option>
                ))}
            </select>
            <img src={`/${thumb}`} alt={thumb} />
            <button type="button" onClick={() => setModalShow(true)}>
              Mudar Imagem
            </button>
            <Modal
              open={modalShow}
              onClose={() => setModalShow(false)}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
            >
              {
                <ImagesWIndow
                  show={modalShow}
                  onHide={() => setModalShow(false)}
                  setThumb={setThumb}
                  setModalShow={setModalShow}
                />
              }
            </Modal>
          </div>
          <div className="updateContent-form">
            <SunEditor
              setContents={content}
              ref={editor}
              lang="pt_br"
              width="92%"
              height="400"
              name="my-editor"
              setOptions={{
                mode: "classic",
                katex: "window.katex",
                imageWidth: "(auto)",
                imageHeight: "(auto)",
                // imageUploadUrl: `${base_url}/ckimage`,
                imageAccept: "*",
                // imageGalleryUrl: `${base_url}/images`,
                imageFileInput: true,
                videoFileInput: false,
                tabDisable: false,
                buttonList: [
                  [
                    "undo",
                    "redo",
                    "font",
                    "fontSize",
                    "formatBlock",
                    "paragraphStyle",
                    "blockquote",
                    "bold",
                    "underline",
                    "italic",
                    "strike",
                    "subscript",
                    "superscript",
                    "fontColor",
                    "hiliteColor",
                    "textStyle",
                    "removeFormat",
                    "outdent",
                    "indent",
                    "align",
                    "horizontalRule",
                    "list",
                    "lineHeight",
                    "table",
                    "link",
                    "image",
                    "video",
                    "audio",
                    "math",
                    "imageGallery",
                    "fullScreen",
                    "showBlocks",
                    "codeView",
                    "preview",
                    "print",
                    "save",
                    "template",
                  ],
                ],
              }}
              onChange={dataForm}
              onImageUploadBefore={(files) => {
                let nameImage = files[0].name;
                // setFile(files[0]);
                editor.current.editor.insertHTML(`<img src="/${nameImage}">`);
                return true;
              }}
            />
          </div>
        </div>
      </Admin>
    </div>
  );
}
