    // }, 500);
    // console.log("aqui");
    // console.log("a");
  // }, [isCategoryCreated]);
  function oi(e){
    refetc
  }

  console.log("fora: ", isCategoryCreated);

  return (
    <div>
      <Admin>
        <div className="category-body">
          <NewCategory isCategoryCreated={oi} />

          {data &&
            data.getAllCategories.map((res, index) => {
              return (
                <div key={index} className="categories">
                  {res.name.toUpperCase()}
                  <IconButton
                    onClick={() => {
                      deleteCategory({ variables: { id: res.id } });
                      setTimeout(() => {
                        refetch();
                      }, 500);
                    }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </div>
              );
            })}
        </div>
      </Admin>
    </div>
  );
}
