import { gql } from "@apollo/client";

export const NEW_CATEGORY = gql`
  mutation createCategory($name: String!) {
    createCategory(name: $name) {
      successfull
    }
  }
`;

export const DELETE_CATEGORY = gql`
  mutation deleteCategory($id: ID!) {
    deleteCategory(id: $id) {
      successfull
    }
  }
`;

export const DELETE_POST = gql`
  mutation deletePost($id: ID!) {
    deletePost(id: $id) {
      successfull
    }
  }
`;

export const UPDATE_INPUT = gql`
  mutation updateInput($id: ID!, $input: String!, $info: Boolean) {
    updateInput(id: $id, input: $input, info: $info) {
      successfull
    }
  }
`;

export const UPDATE_ADMIN_POST_INFO = gql`
  mutation updatePost($id: ID!

    id: { type: GraphQLID },
    name: { type: GraphQLString },
    content: { type: GraphQLString },
    category: { type: GraphQLString },
    slug: { type: GraphQLString },
    slide: { type: GraphQLBoolean },
    middle: { type: GraphQLBoolean },
    gameplay: { type: GraphQLBoolean },
    publicPost: { type: GraphQLBoolean },
    midSection: { type: GraphQLBoolean },
    thumb: { type: GraphQLString },

  ) {
    updatePost(id: $id) {
      name
      content
      category
      slide
      middle
      gameplay
      public
      midSection
      thumb
    }
  }
`;
