import React, { useState } from "react";

import axios from "axios";
import "react-dropzone-uploader/dist/styles.css";
import "./FileDrop.css";
import { base_url } from "../../config";

export default function FileDrop(props) {
  const [File, setFile] = useState("");
  const [fileName, setFileName] = useState("Choose File");
  const [uploadPercentage, setUploadPercentage] = useState();

  function onChange(e) {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
  }

  async function onSubmit(e) {
    e.preventDefault();
    const data = new FormData();
    data.append("file", file);
    const res = await axios.post(`${base_url}/upload`, data, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
      onUploadProgress: (progressEvent) => {
        setUploadPercentage(
          parseInt(
            Math.round((progressEvent.loaded * 100) / progressEvent.total)
          )
        );
      },
    });
    const { fileName, filePath } = res.data;

    setUploadedFile(fileName, filePath);
  }

  return (
    // <Dropzone
    //   getUploadParams={getUploadParams}
    //   onChangeStatus={handleChangeStatus}
    //   onSubmit={handleSubmit}
    //   accept="image/*,audio/*,video/*"
    //   inputContent={"Adicione Fotos"}
    //   inputWithFilesContent={"Procurar Fotos"}
    //   submitButtonContent={"Apagar"}
    // />
    <>
      <div class="mb-3">
        <input
          className="form-control"
          type="file"
          id="formFile"
          onChange={onChange}
        />
        <label className="form-label" htmlFor="formFile">
          {fileName}
        </label>
        <input
          type="submit"
          value="Adicionar"
          class="btn btn-primary btn-block mt-4 col-12"
          onSubmit={onSubmit}
        />
      </div>
    </>
  );
}
