import React, { useEffect, useContext } from "react";
import { useParams, Link } from "react-router-dom";
import "./Frontpage.css";
import "social-share-button";
import { AiFillFacebook, AiOutlineTwitter } from "react-icons/ai";

import Container from "../../components/Container";
import SidebarFront from "../../components/SidebarFront";
import Error from "../ErrorPage";

// import { useInfo } from "../../Contexts/Context";

import { useQuery } from "@apollo/client";
import { GET_INFO_POST } from "../../Graphql/Queries";

interface RouteParams {
  frontpage: string;
}

interface InfoPost {
  id: number;
  name: string;
  content: string;
}

interface InfoPostProp {
  getInfoPost: InfoPost;
}

const Frontpage: React.FC = () => {
  const nameParams = useParams<RouteParams>();
  let name = nameParams.frontpage;

  // const { setIdPost, idPost } = useInfo();

  const { data } = useQuery<InfoPostProp>(GET_INFO_POST, { variables: { slug: name } });

  // useEffect(() => {
  //   if (data) {
  //     setIdPost(data.getInfoPost.id);
  //     console.log("id: ", idPost);
  //   }
  // }, [data]);

  console.log(data);
  console.log(data.getInfoPost[0]);

  if (data)
    return (
      <Container>
        <div className="front-content">
          <div
            className="sun-editor-editable"
            dangerouslySetInnerHTML={{ __html: data.getInfoPost[0].content }}
          />
          <div className="front-sidebar">
            Compartilhe nas redes Sociais!!
            <br />
            <Link
              data-module="SocialShareButton"
              data-module-text={`Divulgue ${name}`}
              data-module-title={name}
              data-module-base-url={`https://www.jogosindie.com.br/${data.getInfoPost[0].name}`}
              data-module-net="facebook"
            >
              <AiFillFacebook size="2.8em" />
            </Link>
            <Link
              data-module="SocialShareButton"
              data-module-text={`Divulgue ${data.getInfoPost[0].name}`}
              data-module-title={name}
              data-module-base-url={`https://www.jogosindie.com.br/${data.getInfoPost[0].name}`}
              data-module-net="twitter"
            >
              <AiOutlineTwitter size="2.8em" />
            </Link>
            <SidebarFront />
          </div>
        </div>
      </Container>
    );
};

export default Frontpage;
