import React, { useState, useEffect, useRef } from "react";
import slugify from "react-slugify";
import { useParams } from "react-router-dom";
import SunEditor from "suneditor-react";

import "suneditor/dist/css/suneditor.min.css"; // Import Sun Editor's CSS File
import axios from "axios";
import Modal from "@mui/material/Modal";

import Admin from "../Admin";
import ImagesWIndow from "../../../Components/ImagesWIndow/ImagesWIndow";
import NewCategory from "../../../Components/NewCategory";

import "./UpdatePost.css";
import { useQuery, useMutation } from "@apollo/client";
import { GET_ADMIN_POST_INFO, GET_ALL_CATEGORIES } from "../../../Graphql/Queries";
import { UPDATE_ADMIN_POST_INFO } from "../../../Graphql/Mutations";

export default function UpdatePost(props) {
  const nameParams = useParams();
  let idNumber = nameParams.id;

  const [infoPost, setInfoPost] = useState({
    name: "",
    content: "",
    category: "",
    slide: false,
    middle: false,
    gameplay: false,
    public: false,
    midSection: false,
  });
  const [thumb, setThumb] = useState("");
  const [file, setFile] = useState("");
  const [modalShow, setModalShow] = useState(false);
  const [reloadPage, setReloadPage] = useState(false);
  const editor = useRef(null);

  function dataForm(data) {
    setInfoPost({ ...infoPost, content: data });
  }

  // useEffect(() => {
  //   let image = file.name;
  //   const url = `${base_url}/upload/${image}`;
  //   const data = new FormData();
  //   data.append("file", file);
  //   const options = {
  //     headers: {
  //       "Content-Type": "multipart/form-data",
  //     },
  //   };
  //   axios.post(url, data, options);
  // }, [file]);

  const { data, refetch } = useQuery(GET_ADMIN_POST_INFO, { variables: { id: idNumber } });
  const { data: categories } = useQuery(GET_ALL_CATEGORIES);

  const [updatePost, { data: isUpdated }] = useMutation(UPDATE_ADMIN_POST_INFO);

  useEffect(() => {
    if (data) {
      setInfoPost({
        name: data.getAdminInfoPost[0].name,
        content: data.getAdminInfoPost[0].content,
        category: data.getAdminInfoPost[0].category,
        slide: data.getAdminInfoPost[0].slide,
        middle: data.getAdminInfoPost[0].middle,
        gameplay: data.getAdminInfoPost[0].gameplay,
        public: data.getAdminInfoPost[0].public,
        midSection: data.getAdminInfoPost[0].midSection,
      });
      setThumb(data.getAdminInfoPost[0].thumb);
    }
  }, [data]);

  // useEffect(() => {
  // if (isUpdated && isUpdated.updatePost.successfull) {
  // refetch();
  // }
  // }, [isUpdated]);
  console.log(isUpdated);

  function reload() {
    setReloadPage(true);
  }
  console.log(data);
  console.log("conteudo: ", infoPost);

  return (
    <div>
      <Admin>
        <div className="updateContent">
          <div className="updateContent-field">
            <p className="update-warning">
              {isUpdated && isUpdated.updatePost.successfull ? "" : "Atualizado"}{" "}
            </p>
            <button

            >
              Atualizar
            </button>
            Nome:
            <input
              type="text"
              onChange={(e) => setInfoPost({ ...infoPost, name: e.target.value })}
              value={infoPost.name}
            />
            <div>
              Publico
              <input
                type="checkbox"
                onClick={(e) => setInfoPost({ ...infoPost, public: e.target.checked })}
                defaultChecked={infoPost.public ? true : null}
              />
            </div>
            <div>
              Slide
              <input
                type="checkbox"
                onClick={(e) => setInfoPost({ ...infoPost, slide: e.target.checked })}
                defaultChecked={infoPost.slide ? true : null}
              />
              Meio
              <input
                type="checkbox"
                onClick={(e) => setInfoPost({ ...infoPost, middle: e.target.checked })}
                defaultChecked={infoPost.middle ? true : null}
              />
              Gameplay
              <input
                type="checkbox"
                onClick={(e) => setInfoPost({ ...infoPost, gameplay: e.target.checked })}
                defaultChecked={infoPost.gameplay ? true : null}
              />
              midSection
              <input
                type="checkbox"
                onClick={(e) => setInfoPost({ ...infoPost, midSection: e.target.checked })}
                defaultChecked={infoPost.gameplay ? true : null}
              />
            </div>
            <NewCategory />
            <select
              value={infoPost.category}
              onChange={(e) => setInfoPost({ ...infoPost, category: e.target.value })}
            >
              <option>Escolha uma Categoria</option>
              {categories &&
                categories.getAllCategories.map((cat, i) => (
                  <option key={i}>{cat.name.toUpperCase()}</option>
                ))}
            </select>
            <img src={`/${thumb}`} alt={thumb} />
            <button type="button" onClick={() => setModalShow(true)}>
              Mudar Imagem
            </button>
            <Modal
              open={modalShow}
              onClose={() => setModalShow(false)}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
            >
              {
                <ImagesWIndow
                  show={modalShow}
                  onHide={() => setModalShow(false)}
                  setThumb={setThumb}
                  setModalShow={setModalShow}
                />
              }
            </Modal>
          </div>
          <div className="updateContent-form">
            <SunEditor
              setContents={infoPost.content}
              ref={editor}
              lang="pt_br"
              width="92%"
              height="400"
              name="my-editor"
              setOptions={{
                mode: "classic",
                katex: "window.katex",
                imageWidth: "(auto)",
                imageHeight: "(auto)",
                // imageUploadUrl: `${base_url}/ckimage`,
                imageAccept: "*",
                // imageGalleryUrl: `${base_url}/images`,
                imageFileInput: true,
                videoFileInput: false,
                tabDisable: false,
                buttonList: [
                  [
                    "undo",
                    "redo",
                    "font",
                    "fontSize",
                    "formatBlock",
                    "paragraphStyle",
                    "blockquote",
                    "bold",
                    "underline",
                    "italic",
                    "strike",
                    "subscript",
                    "superscript",
                    "fontColor",
                    "hiliteColor",
                    "textStyle",
                    "removeFormat",
                    "outdent",
                    "indent",
                    "align",
                    "horizontalRule",
                    "list",
                    "lineHeight",
                    "table",
                    "link",
                    "image",
                    "video",
                    "audio",
                    "math",
                    "imageGallery",
                    "fullScreen",
                    "showBlocks",
                    "codeView",
                    "preview",
                    "print",
                    "save",
                    "template",
                  ],
                ],
              }}
              onChange={dataForm}
              onImageUploadBefore={(files) => {
                let nameImage = files[0].name;
                setFile(files[0]);
                editor.current.editor.insertHTML(`<img src="/${nameImage}">`);
                return true;
              }}
            />
          </div>
        </div>
      </Admin>
    </div>
  );
}
