import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import { Link } from "react-router-dom";
import Blocks from "../../../Services/Blocks"

import "./GamePlays.css";


const Gameplay = () => {
  const [images, setImages] = useState([]);
  const [name, setName] = useState([]);
  const [slug, setSlug] = useState([]);

  useEffect(() => {
    Blocks.gameplay().then((res) => {
      setImages(res.map((res) => res.images));
      setName(res.map((res) => res.name));
      setSlug(res.map((res) => res.slug));
    });
  }, []);

  const settings = {
    dots: false,
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    speed: 500,
    arrows: false,
    autoplay: true,
    vertical: true,
    autoplaySpeed: 3000,
    cssEase: "linear",
    verticalSwiping: true,   
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
          vertical: false,
          infinite: true,
          dots: true,
          arrows: false,
        },
      },
      {
        breakpoint: 767,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
          vertical: true,
          infinite: true,
          dots: true,
          arrows: false,
        },
      },
    ],
  };

  return (
    <div className="gameplay">
      <Slider {...settings}>
        {name.map((res, i) => {
          return(
            <div key={i} className='gameplay-block'>                 
                <img src={images[i]} alt={res} />
              <Link to={`${slug[i]}`} className="gameplay-title" style={{ textDecoration: "none" }}>
                <span>{res}</span>
              </Link>
            </div>
          )          
          })}
      </Slider>
    </div>
  );
};

export default Gameplay;
