import { gql } from "@apollo/client";

export const GET_ALL_CATEGORIES = gql`
  query {
    getAllCategories {
      id
      name
    }
  }
`;

export const GET_ALL_POSTS_SLIDE = gql`
  query {
    getAllPosts(field: "slide", answer: true) {
      name
      thumb
      slug
    }
  }
`;

export const GET_ALL_POSTS_MIDDLE = gql`
  query {
    getAllPosts(field: "middle", answer: true) {
      name
      thumb
      slug
    }
  }
`;

export const GET_ALL_POSTS_GAMEPLAY = gql`
  query {
    getAllPosts(field: "gameplay", answer: true) {
      name
      thumb
      slug
    }
  }
`;

export const GET_ALL_POSTS_MIDSECTION = gql`
  query {
    getAllPosts(field: "midSection", answer: true) {
      name
      thumb
      slug
    }
  }
`;

export const GET_INFO_POST = gql`
  query getInfoPost{
    getInfoPost(slug: "valentina") {
    id
    name
    content
    thumb
  }
}
  }
`;
