import React, { useState } from "react";

import axios from "axios";
// import "react-dropzone-uploader/dist/styles.css";
import "./FileDrop.css";
import { base_url } from "../../config";
import { ApolloClient, ApolloProvider, InMemoryCache, useMutation } from "@apollo/client";
import { UPLOAD_IMAGE } from "../../Graphql/Mutations";

import { createUploadLink } from "apollo-upload-client";

const client = new ApolloClient({
  link: createUploadLink({
    uri: `http://localhost:3001/`,
  }),
  cache: new InMemoryCache(),
});

export default function FileDrop(props) {
  const [file, setFile] = useState("");
  const [fileName, setFileName] = useState("");
  const [uploadedFile, setUploadedFile] = useState({});
  const [message, setMessage] = useState("");
  const [uploadPercentage, setUploadPercentage] = useState();

  function onChange(e) {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
  }

  const [sendFile] = useMutation(UPLOAD_IMAGE, {
    onCompleted: (data) => console.log("foto: ", data),
  });
