import { base_url } from "../config";

const UpdateService = {
  category: () => {
    return fetch(`${base_url}/category`)
      .then((res) => res.json(res))
      .then((res) => {
        return res;
      });
  },
  getInfo: (idNumber) => {
    return fetch(`${base_url}/updatepost/${idNumber}`)
      .then((res) => res.json())
      .then((res) => {
        return res;
      });
  },
  update: (
    id,
    content,
    namePost,
    selectedCategory,
    newPost,
    publicPost,
    slugify
  ) => {
    return fetch(`${base_url}/updatepost/${id}`, {
      method: "put",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        id: id,
        name: namePost,
        content: content,
        category: selectedCategory,
        newpost: newPost ? 1 : 0,
        slug: slugify,
        publicPost: publicPost ? 1 : 0,
      }),
    })
      .then((res) => res.json())
      .then((res) => {
        return res;
      });
  },
};

export default UpdateService;
