import React, { useState, useEffect, useRef } from "react";
import slugify from "react-slugify";
import { useHistory } from "react-router-dom";
import SunEditor from "suneditor-react";

import "suneditor/dist/css/suneditor.min.css"; // Import Sun Editor's CSS File
// import axios from "axios";
import Modal from "@mui/material/Modal";

import Admin from "../Admin";
import ImagesWIndow from "../../../Components/ImagesWIndow/ImagesWIndow";
import NewCategory from "../../../Components/NewCategory";

import "./NewPost.css";
import { useQuery, useMutation } from "@apollo/client";
import { GET_ALL_CATEGORIES } from "../../../Graphql/Queries";
import { CREATE_POST } from "../../../Graphql/Mutations";

export default function UpdatePost(props) {
  let history = useHistory();
  const [name, setName] = useState("te");
  const [content, setContent] = useState("te");
  const [category, setCategory] = useState("te");
  const [isscheduled] = useState(true);
  const [schedule, setSchedule] = useState("");
  const [slide, setSlide] = useState(false);
  const [middle, setMiddle] = useState(true);
  const [gameplay, setGameplay] = useState(true);
  const [midSection, setMidSection] = useState(false);
  const [thumb, setThumb] = useState("foto.jpg");
  // const [file, setFile] = useState("");
  const [modalShow, setModalShow] = useState(false);
  const editor = useRef(null);

  function dataForm(data) {
    setContent(data);
  }

  // useEffect(() => {
  //   let image = file.name;
  //   const url = `${base_url}/upload/${image}`;
  //   const data = new FormData();
  //   data.append("file", file);
  //   const options = {
  //     headers: {
  //       "Content-Type": "multipart/form-data",
  //     },
  //   };
  //   axios.post(url, data, options);
  // }, [file]);

  const { data: categories } = useQuery(GET_ALL_CATEGORIES);

  const [createPost, { data: isCreated }] = useMutation(CREATE_POST);

  function create() {

    createPost({
      variables: {
        name: name,
        content: content,
        category: category,
        slug: slugify(name, { delimiter: "-" }),
        scheduled: isscheduled,
        schedule: schedule,
        slide: slide,
        middle: middle,
        gameplay: gameplay,
        publicPost: isscheduled,
        midSection: midSection,
        thumb: thumb,
      },
    });
  }

  function red() {

  }

  useEffect(() => {
    if (isCreated) {
      const newPostId = isCreated.createPost.id;

      history.push(`/admin/updatepost/${newPostId}`);

    }
  }, [isCreated]);

  return (
    <div>
      <Admin>
        <div className="updateContent">
          <div className="updateContent-field">
            <button onClick={create}>Publicar</button>
            Nome:
            <input type="text" onChange={(e) => setName(e.target.value)} value={name} />
            <div>
              Agendar
              {/* <input type="checkbox" onClick={(e) => setisScheduled(e.target.checked)} /> */}
              {schedule ? (
                <input
                  type="datetime-local"
                  className={schedule ? "schedule-show" : "schedule"}
                  onChange={(e) => setSchedule(e.target.value)}
                />
              ) : null}
              Slide
              <input
                type="checkbox"
                onClick={(e) => setSlide(e.target.checked)}
                defaultChecked={slide ? true : null}
              />
              Meio
              <input
                type="checkbox"
                onClick={(e) => setMiddle(e.target.checked)}
                defaultChecked={middle ? true : null}
              />
              Gameplay
              <input
                type="checkbox"
                onClick={(e) => setGameplay(e.target.checked)}
                defaultChecked={gameplay ? true : null}
              />
              midSection
              <input
                type="checkbox"
                onClick={(e) => setMidSection(e.target.checked)}
                defaultChecked={midSection ? true : null}
              />
            </div>
            <NewCategory />
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
              <option>Escolha uma Categoria</option>
              {categories &&
                categories.getAllCategories.map((cat, i) => (
                  <option key={i}>{cat.name.toUpperCase()}</option>
                ))}
            </select>
            <img src={`/${thumb}`} alt={thumb} />
            <button type="button" onClick={() => setModalShow(true)}>
              Mudar Imagem
            </button>
            <Modal
              open={modalShow}
              onClose={() => setModalShow(false)}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
            >
              {
                <ImagesWIndow
                  show={modalShow}
                  onHide={() => setModalShow(false)}
                  setThumb={setThumb}
                  setModalShow={setModalShow}
                />
              }
            </Modal>
          </div>
          <div className="updateContent-form">
            <SunEditor
              setContents={content}
              ref={editor}
              lang="pt_br"
              width="92%"
              height="400"
              name="my-editor"
              setOptions={{
                mode: "classic",
                katex: "window.katex",
                imageWidth: "(auto)",
                imageHeight: "(auto)",
                // imageUploadUrl: `${base_url}/ckimage`,
                imageAccept: "*",
                // imageGalleryUrl: `${base_url}/images`,
                imageFileInput: true,
                videoFileInput: false,
                tabDisable: false,
                buttonList: [
                  [
                    "undo",
                    "redo",
                    "font",
                    "fontSize",
                    "formatBlock",
                    "paragraphStyle",
                    "blockquote",
                    "bold",
                    "underline",
                    "italic",
                    "strike",
                    "subscript",
                    "superscript",
                    "fontColor",
                    "hiliteColor",
                    "textStyle",
                    "removeFormat",
                    "outdent",
                    "indent",
                    "align",
                    "horizontalRule",
                    "list",
                    "lineHeight",
                    "table",
                    "link",
                    "image",
                    "video",
                    "audio",
                    "math",
                    "imageGallery",
                    "fullScreen",
                    "showBlocks",
                    "codeView",
                    "preview",
                    "print",
                    "save",
                    "template",
                  ],
                ],
              }}
              onChange={dataForm}
              onImageUploadBefore={(files) => {
                let nameImage = files[0].name;
                // setFile(files[0]);
                editor.current.editor.insertHTML(`<img src="/${nameImage}">`);
                return true;
              }}
            />
          </div>
        </div>
      </Admin>
    </div>
  );
}
