import React, { useState, useEffect, useRef } from "react";
import SunEditor from "suneditor-react";
import slugify from "react-slugify";
import { Redirect } from "react-router-dom";

import "suneditor/dist/css/suneditor.min.css"; // Import Sun Editor's CSS File
import axios from "axios";
import Modal from "@material-ui/core/Modal";
import { base_url } from "../../../config";

import Admin from "../Admin";
import ImagesWIndow from "../../../Components/ImagesWIndow/ImagesWIndow";
import NewCategory from "../../../Components/NewCategory/NewCategory";
import NewPostService from "../../../Services/NewPost";
import CategoryService from "../../../Services/Category";

import "./NewPost.css";
var moment = require("moment");

export default function NewPost(props) {
  var scheduleTime = moment().format("YYYY-MM-DD HH:mm");
  // const [file, setFile] = useState("");
  const [namePost, setNamePost] = useState("");
  const [content, setContent] = useState("");
  const [mainimage, setMainimage] = useState("");
  const [categories, setCategories] = useState([]);
  const [word, setWord] = useState("");
  const [modalShow, setModalShow] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [isAdded, setIsAdded] = useState(false);
  const [newName, setNewName] = useState("");
  const [datetime, setDatetime] = useState("");
  const [id, setId] = useState("");
  const [schedule, setSchedule] = useState(false);

  const editor = useRef(null);

  function dataForm(data) {
    setContent(data);
  }

  function createPost() {
    namePost && content && selectedCategory && mainimage
      ? NewPostService.new(
          namePost,
          content,
          selectedCategory,
          mainimage,
          newName ? 1 : 0,
          slugify(namePost, { delimiter: "-" }),
          schedule ? datetime : scheduleTime,
          schedule ? 0 : 1
          // name, newpost, gameplay, gallery, images, category, featured, id, slug
        )
          .then((res) => {
            if (res.insertId) {
              setId(res.insertId);
              setIsAdded(true);
            } else {
              alert(res);
              setIsAdded(false);
            }
          })
          .catch((error) => {
            console.log("Erro ao adicionar o post");
          })
      : alert("HÃ¡ campos vazios");
  }

  useEffect(() => {
    CategoryService.get().then((res) => {
      setCategories(res);
    });
  }, [word]);

  function sendFile(file) {
    const url = `${base_url}/images/image`;
    const data = new FormData();
    data.append("file", file);
    const options = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };
    axios.post(url, data, options);
  }

  function schedulePost(res) {
    setSchedule(res);
  }

  return (
    <div>
      {isAdded ? <Redirect to={`/admin/updatepost/${id}`} /> : null}
      <Admin>
        <div className="newContent">
          <div className="newContent-field">
            <button onClick={createPost}>
              {schedule ? "Agendar" : "Publicar"}
            </button>
            Nome:
            <input type="text" onChange={(e) => setNamePost(e.target.value)} />
            Agendar:
            <input
              type="checkbox"
              onClick={(e) => schedulePost(e.target.checked)}
            />
            <input
              type="datetime-local"
              id={schedule ? "schedule-show" : "schedule"}
              onChange={(e) => setDatetime(e.target.value)}
            />
            Novidade
            <input
              type="checkbox"
              onClick={(e) => setNewName(e.target.checked)}
            />
            <NewCategory addCategory={setWord} />
            <select onChange={(e) => setSelectedCategory(e.target.value)}>
              <option>Escolha uma Categoria</option>
              {categories.map((res, i) => (
                <option className="option-category" key={i} value={res}>
                  {res.name}
                </option>
              ))}
            </select>
            <img src={`/${mainimage}`} alt={mainimage} />
            <div>
              <button type="button" onClick={() => setModalShow(true)}>
                Adicionar Imagem
              </button>
              <Modal
                open={modalShow}
                onClose={() => setModalShow(false)}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
              >
                {
                  <ImagesWIndow
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                    setMainimage={setMainimage}
                    setModalShow={setModalShow}
                  />
                }
              </Modal>
            </div>
          </div>
          <div className="newContent-form">
            <SunEditor
              ref={editor}
              lang="pt_br"
              width="100%"
              height="400"
              name="my-editor"
              setOptions={{
                mode: "classic",
                katex: "window.katex",
                imageWidth: "(auto)",
                imageHeight: "(auto)",
                // imageUploadUrl: `${base_url}upload`,
                imageAccept: "*",
                imageGalleryUrl: `${base_url}/images`,
                imageFileInput: true,
                videoFileInput: false,
                tabDisable: false,
                buttonList: [
                  [
                    "undo",
                    "redo",
                    "font",
                    "fontSize",
                    "formatBlock",
                    "paragraphStyle",
                    "blockquote",
                    "bold",
                    "underline",
                    "italic",
                    "strike",
                    "subscript",
                    "superscript",
                    "fontColor",
                    "hiliteColor",
                    "textStyle",
                    "removeFormat",
                    "outdent",
                    "indent",
                    "align",
                    "horizontalRule",
                    "list",
                    "lineHeight",
                    "table",
                    "link",
                    "image",
                    "video",
                    "audio",
                    "math",
                    "imageGallery",
                    "fullScreen",
                    "showBlocks",
                    "codeView",
                    "preview",
                    "print",
                    "save",
                    "template",
                  ],
                ],
              }}
              onChange={dataForm}
              onImageUploadBefore={(files) => {
                let nameImage = files[0].name;
                sendFile(files[0]);
                editor.current.editor.insertHTML(`<img src="/${nameImage}">`);
                return true;
              }}
            />
          </div>
        </div>
      </Admin>
    </div>
  );
}
