import React, { useState } from "react";
import "./NewCategory.css";
import Category from "../../Services/Category";

import { useMutation } from "@apollo/client";
import { NEW_CATEGORY } from "../../Graphql/Mutations";

export default function NewCategory(props) {
  const [wordInput, setWordInput] = useState("");
  const [createCategory, { error }] = useMutation(NEW_CATEGORY);

  // function addCategory() {
  //   Category.add(wordInput);
  // }

  return (
    <div className="new-category">
      Digite a nova categoria:
      <input
        type="text"
        name="name"
        value={wordInput}
        onChange={(e) => (e ? setWordInput(e.target.value) : null)}
      />
      <button
        onClick={
          wordInput
            ? () => {
                createCategory({ variables: { name: wordInput } });
                props.refetch("");
                setWordInput("");
              }
            : null
        }
      >
        Adicionar
      </button>
    </div>
  );
}
