import React, { useState, useEffect } from "react";
import Admin from "../Admin";
import NewCategory from "../../../Components/NewCategory/NewCategory";
import "./Categories.css";

import { useQuery, useMutation } from "@apollo/client";
import { GET_ALL_CATEGORIES } from "../../../Graphql/Queries";
import { DELETE_CATEGORY } from "../../../Graphql/Mutations";

import DeleteIcon from "@mui/icons-material/Delete";
import { IconButton } from "@mui/material";
// import CategoryService from "../../../Services/Category";

export default function Categories(props) {
  const { data, refetch } = useQuery(GET_ALL_CATEGORIES);
  const [isCategoryCreated, setIsCategoryCreated] = useState(false);
  const [deleteCategory, { error }] = useMutation(DELETE_CATEGORY);

  useEffect(() => {
    console.log(isCategoryCreated);

    if (isCategoryCreated) {
      refetch();
    }
  }, [isCategoryCreated]);

  return (
    <div>
      <Admin>
        <div className="category-body">
          <NewCategory isCategoryCreated={setIsCategoryCreated} />

          {data &&
            data.getAllCategories.map((res, index) => {
              return (
                <div key={index} className="categories">
                  {res.name.toUpperCase()}
                  <IconButton
                    onClick={() => {
                      deleteCategory({ variables: { id: res.id } });
                      refetch();
                    }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </div>
              );
            })}
        </div>
      </Admin>
    </div>
  );
}
