import React, { useState } from "react";
import Dropzone from "react-dropzone-uploader";

import axios from "axios";
import "react-dropzone-uploader/dist/styles.css";
import "./FileDrop.css";
import { base_url } from "../../config";

export default function FileDrop(props) {
  const [uploadedFile, setUploadedFile] = useState({});

  const getUploadParams = async ({ file }) => {
    console.log("file: ", file.name);
    const data = new FormData();
    data.append("file", file);
    const res = await axios.post(`${base_url}/upload`, data, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
      onUploadProgress: (progressEvent) => {
        setUploadPercentage(parseInt(Math.round(progressEvent.loaded * 10)));
      },
    });
    const { fileName, filePath } = res.data;

    setUploadedFile(fileName, filePath);
  };

  // called every time a file's `status` changes
  // const handleChangeStatus = ({ meta, file }, status) => { console.log(status, meta, file) }
  const handleChangeStatus = ({ xhr }) => {
    console.log(xhr);
  };

  // receives array of files that are done uploading when submit button is clicked
  const handleSubmit = (files, allFiles) => {
    // console.log(files.map((f) => f.meta));
    allFiles.forEach((f) => f.remove());
  };

  return (
    <Dropzone
      getUploadParams={getUploadParams}
      onChangeStatus={handleChangeStatus}
      onSubmit={handleSubmit}
      accept="image/*,audio/*,video/*"
      inputContent={"Adicione Fotos"}
      inputWithFilesContent={"Procurar Fotos"}
      submitButtonContent={"Apagar"}
    />
  );
}
