import React, { useState, useEffect } from "react";
import Admin from "../Admin";
import NewCategory from "../../../Components/NewCategory/NewCategory";
import "./Categories.css";

import { useQuery, useMutation } from "@apollo/client";
import { GET_ALL_CATEGORIES } from "../../../Graphql/Queries";
import { DELETE_CATEGORY } from "../../../Graphql/Mutations";

import DeleteIcon from "@mui/icons-material/Delete";
import { IconButton } from "@mui/material";
import {InfoContext} from "../../../Contexts/Context"

export default function Categories(props) {
  const { data, refetch } = useQuery(GET_ALL_CATEGORIES);
  const [isCategoryAdded, setIsCategoryAdded] = useState(false);
  const [deleteCategory, { error, data: del }] = useMutation(DELETE_CATEGORY);

  const { setCategoryAdded } = useContext(InfoContext);


  // useEffect(() => {
  // setTimeout(() => {
  //   refetch();
  // }, 500);
  // console.log("aqui");
  // console.log("a");
  // }, [isCategoryCreated]);
  useEffect(() => {
    console.log(isCategoryAdded);
    if (isCategoryAdded) {
      refetch();
    }
  }, [isCategoryAdded]);

  useEffect(() => {
    if (del && del.deleteCategory.successfull) {
      refetch();
    }
  }, [del]);

  return (
    <div>
      <Admin>
        <div className="category-body">
          <NewCategory />

          {data &&
            data.getAllCategories.map((res, index) => {
              return (
                <div key={index} className="categories">
                  {res.name.toUpperCase()}
                  <IconButton
                    onClick={() => {
                      deleteCategory({ variables: { id: res.id } });
                    }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </div>
              );
            })}
        </div>
      </Admin>
    </div>
  );
}
