import React, { useState, useEffect } from "react";
import Admin from "../Admin";
import NewCategory from "../../../Components/NewCategory/NewCategory";
import "./Categories.css";

import { useQuery, useMutation } from "@apollo/client";
import { GET_ALL_CATEGORIES } from "../../../Graphql/Queries";
import { DELETE_CATEGORY } from "../../../Graphql/Mutations";

import DeleteIcon from "@mui/icons-material/Delete";
import { IconButton } from "@mui/material";
// import CategoryService from "../../../Services/Category";

export default function Categories(props) {
  // const [loaded, setLoaded] = useState(false);
  const [word, setWord] = useState([]);
  const [deleteCategory, { error }] = useMutation(DELETE_CATEGORY);

  // useEffect(() => {
  //   CategoryService.get().then((res) => {
  //     setCategories(res);
  //     setLoaded(true);
  //   });
  // }, [word]);

  // function deleteCategory(name) {
  //   CategoryService.delete(name);
  //   setCategories(categories.filter((item) => item === name));
  // }

  const { data } = useQuery(GET_ALL_CATEGORIES);

  return (
    <div>
      <Admin>
        <div className="category-body">
          <NewCategory addCategory={setWord} />
          {data &&
            data.getAllCategories.map((res, index) => {
              console.log(res);
              return (
                <div key={index} className="categories">
                  {res.name.toUpperCase()}
                  <IconButton
                    onClick={() => {
                      deleteCategory({ variables: { id: res.id } });
                    }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </div>
              );
            })}
        </div>
      </Admin>
    </div>
  );
}
