import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Admin from "./../Admin";

import "./Posts.css";
import PostsService from "../../../Services/Posts";

export default function Posts() {
  const [posts, setPosts] = useState([]);
  const [newgame, setNewgame] = useState("");
  const [recommendation, setRecommendation] = useState("");
  const [gameplay, setGameplay] = useState("");
  const [gallery, setGallery] = useState("");
  const [id, setId] = useState("");
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    PostsService.posts().then((res) => {
      setPosts(res.map((res) => res.name));
      setNewgame(res.map((res) => res.newpost));
      setRecommendation(res.map((res) => res.featured));
      setGameplay(res.map((res) => res.gameplay));
      setGallery(res.map((res) => res.gallery));
      setId(res.map((res) => res.id));
    });
  }, []);
  // name, newpost, gameplay, gallery, image, category, featured, id, slug

  useEffect(() => {
    PostsService.schedule().then((res) => {
      setSchedule(res.map((res) => res.date));
    });
  }, []);

  function deletePost(id) {
    PostsService.schedule(id);
    setPosts(posts.filter((item) => item !== deletePost));
  }

  function updateOption(place, option, name) {
    PostsService.updateOption(place, option, name);
  }

  return (
    <>
      <Admin>
        <ul>
          {posts.map((res, i) => {
            return (
              <div key={i} className="posts">
                <div>
                  <Link
                    to={`/admin/updatepost/${id[i]}`}
                    key={i}
                    className="posts-name"
                    style={{ textDecoration: "none" }}
                  >
                    {res}
                  </Link>
                </div>
                <div>{schedule[i]}</div>
                <div>
                  <span>Novidade</span>
                  <input
                    type="checkbox"
                    onClick={(e) =>
                      updateOption("newgame", e.target.checked, posts[i])
                    }
                    defaultChecked={newgame[i] ? true : null}
                  />
                </div>
                <div>
                  <span> Destaque</span>
                  <input
                    type="checkbox"
                    onClick={(e) =>
                      updateOption("recommendation", e.target.checked, posts[i])
                    }
                    defaultChecked={recommendation[i] ? true : null}
                  />
                </div>
                <div>
                  <span>Gameplay</span>
                  <input
                    type="checkbox"
                    onClick={(e) =>
                      updateOption("gameplay", e.target.checked, posts[i])
                    }
                    defaultChecked={gameplay[i] ? true : null}
                  />
                </div>
                <div>
                  <span>Galeria</span>
                  <input
                    type="checkbox"
                    onClick={(e) =>
                      updateOption("gallery", e.target.checked, posts[i])
                    }
                    defaultChecked={gallery[i] ? true : null}
                  />
                </div>
                <button onClick={() => deletePost(id[i])}>Apagar</button>
              </div>
            );
          })}
        </ul>
      </Admin>
    </>
  );
}
